from MainClasss import *from pygame import RectSPEED = 1class Player(Person, Sprite):    def __init__(self, name, coord, way_to_image, way_name, armor=None):        Sprite.__init__(self)        self.f = True        super().__init__('sprite/person_sprites/forward/Player/Player_forward_1.bmp', coord, 200, 200, 100, 100,                         name, way_to_image, way_name, armor)        # sites moving lists        # downloading sprites        self.inventory = Inventory((4, 10))        # rect and move        self.rect = Rect((0, 25, 20, 5))        self.speed_boost = False        # draw and animation        self.collision_x_site = 0        self.collision_y_site = 0        self.revers_frame = False        self.frame = 0        self.count_next_frame = 0    def update(self, left, right, up, down, platforms, shift):        if self.die_f is False:            if shift and self.food > 0:                speed = SPEED * 3                self.speed_boost = True            else:                speed = SPEED                self.speed_boost = False            if left:                self.x_vel = -speed            if right:                self.x_vel = speed            if not (left or right):                self.x_vel = 0            if up:                self.y_vel = -speed            if down:                self.y_vel = speed            if not (up or down):                self.y_vel = 0            self.eat += abs(self.x_vel)            self.eat += abs(self.y_vel)            if self.eat >= 1000:                self.eat = 0                self.hunger(1)            self.rect.x += self.x_vel            # проверяем что ни скем не столкнулись            self.collide(self.x_vel, 0, platforms)            # движемся по игрику            self.rect.y += self.y_vel            # проверяем что ни скем не столкнулись            self.collide(0, self.y_vel, platforms)            # отрисовываем анимацию        else:            if self.f:                self.f = False                self.rect.x += 5        self.draw()    def get_coord(self):        return self.rect.x, self.rect.y - 25    def get_rect(self):        return Rect(self.rect.x, self.rect.y - 25, 20, 30)