from MainClasss import *from pygame import RectSPEED = 1class Player(Person, Sprite):    def __init__(self, name, coord, way_to_image, way_name, armor=None):        Sprite.__init__(self)        self.f = True        super().__init__('sprite/person_sprites/forward/Player/Player_forward_1.bmp', coord, 200, 200, 100, 100,                         name, way_to_image, way_name, armor)        # sites moving lists        # downloading sprites        self.add_type('MainHero')        # rect and move        self.rect = Rect((0, 25, 20, 5))        self.speed_boost = False        self.weapon = None        # draw and animation        self.inventory = Inventory((10, 5))        item = Item('sprite/Weapon_sprites/AR15.png', (0, 0), 'Оружие', 1, 'Оно стреляет', 1)        self.inventory.add_item(item)        self.collision_x_site = 0        self.die_f = False        self.collision_y_site = 0        self.revers_frame = False        self.frame = 0        self.count_next_frame = 0    def update(self, left, right, up, down, platforms, shift):        if self.heal_point <= 0:            self.die_f = True        if not self.die_f:            if shift and self.food > 0:                speed = SPEED * 3                self.speed_boost = True            else:                speed = SPEED                self.speed_boost = False            if left:                self.x_vel = -speed            if right:                self.x_vel = speed            if not (left or right):                self.x_vel = 0            if up:                self.y_vel = -speed            if down:                self.y_vel = speed            if not (up or down):                self.y_vel = 0            self.eat += abs(self.x_vel)            self.eat += abs(self.y_vel)            if self.eat >= 1000:                self.eat = 0                self.hunger(1)            self.rect.x += self.x_vel            # проверяем что ни скем не столкнулись            self.collide(self.x_vel, 0, platforms)            # движемся по игрику            self.rect.y += self.y_vel            # проверяем что ни скем не столкнулись            self.collide(0, self.y_vel, platforms)            # отрисовываем анимацию        else:            if self.f:                self.f = False                self.rect.x += 5        self.draw()        if self.weapon is not None:            if not up:                self.weapon.set_layer(self.layer + 1)            else:                self.weapon.set_layer(self.layer - 1)    def get_coord(self):        return self.rect.x, self.rect.y - 25    def get_rect(self):        return Rect(self.rect.x, self.rect.y - 25, 20, 30)    def next_frame(self):        self.count_next_frame += 1        if (self.count_next_frame > 4 and not self.speed_boost) or (self.count_next_frame > 2 and self.speed_boost):            self.count_next_frame = 0            self.frame += 1        if self.frame == len(self.frames_forward) - 1:            self.frame = 0